import React, { useEffect } from "react";
import API from "../utils/API";

function Saved() {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    loadFavorites();
  }, []);

  function loadFavorites() {
    API.getFavorites()
      .then(favorites => {
        setFavorites(favorites.data.results);
      })
      .catch(err => console.log(err));
  }

  return (
    <div>
        <div className="container-fluid">
          <div className="text-center">
            <h2>Your Favorites</h2>
            {favorites.map((book) => {
              return (
                <div className="row">
            <div className="card w-100" key={book.id}>
                <div className="card-body">
                    <img src={props.image} alt="cover" />
                    <small>ISBN: {props.isbn}  |  ID: {props.id}</small>
                    <h3 className="card-text">{props.title}</h3>
                    <h4 className="card-text">{props.authors}</h4>
                    <p className="card-text">{props.description}</p>
                    <div className="d-flex w-100 justify-content-between">
                    <a href={props.link} target="_blank" rel="noreferrer" className="btn btn-outline-dark">More details</a>
                    <button onClick={props.handleAddFavorite} className="btn btn-outline-dark">Add to Favorites</button>
                    </div>
                </div>
            </div>
        </div>
              )
            })}
          </div>
        </div>
    </div>
  );
}

export default Saved;
