import React, { Component } from "react";
import SearchBar from "../components/SearchBar";
import ResultsContainer from "../components/ResultsContainer";
import API from '../utils/API';

class Search extends Component {
  state = {
    books: [],
    query: ""
  }

  handleInputChange = event => {
    const value = event.target.value;
    this.setState({
      query: value
    });
  };

  getBooks = () => {
    API.searchGoogle(this.state.query)
      .then((res) => {
        const books = res.data;
        console.log(books);
      });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    this.getBooks();
  };

  // handleBookSave = id => {
  //   const book = this.state.books.find(book => book.id === id);

  //   API.saveBook({
  //     googleId: book.id,
  //     title: book.volumeInfo.title,
  //     subtitle: book.volumeInfo.subtitle,
  //     link: book.volumeInfo.infoLink,
  //     authors: book.volumeInfo.authors,
  //     description: book.volumeInfo.description,
  //     image: book.volumeInfo.imageLinks.thumbnail
  //   }).then(() => this.getBooks());
  // };

  render() {
    return (
    <div>
        <div className="container-fluid">
          <div className="text-center">
            <h2>Search for Books</h2>
          </div>
          <SearchBar
            handleInputChange={this.handleInputChange}
            handleFormSubmit={this.handleFormSubmit}
            query={this.state.query} />
        </div>
        <ResultsContainer>
            {books.map(book => (
                <div className="card w-100" key={book.id}>
                    <div className="card-body">
                      <img src={book.volumeInfo.imageLinks.thumbnail} alt="cover" />
                      <small>{book.volumeInfo.industryIdentifiers[0].type}: {book.volumeInfo.industryIdentifiers[0].identifier}</small>
                      <h3 className="card-text">{book.volumeInfo.title}</h3>
                      <h4 className="card-text">{book.volumeInfo.authors}</h4>
                      <p className="card-text">{book.volumeInfo.description}</p>
                      <div className="d-flex w-100 justify-content-between">
                        <a href={book.volumeInfo.infoLink} className="btn btn-outline-dark">More details</a>
                        <a href="http://www.google.com" className="btn btn-outline-dark">Add to Favorites</a>
                      </div>
                    </div>
                </div>
            ))}
        </ResultsContainer>
    </div>
    );
  }
}

export default Search;
